function bossbuff() if player.Class1 == 2 and player.Class2 == 7 then -- warden/warrior inventory:useItem(204462) yrest(5000) inventory:useItem(204463) yrest(5000) player:cast("WARDEN_SAVAGE_POWER") player:cast("SCOUT_ARROW_OF_ESSENCE") end if player.Class1 == 4 and player.Class2 == 5 then -- mage/priest player:cast("MAGE_ENERGY_INFLUX") player:cast("MAGE_INTENSIFICATION") player:cast("MAGE_MAGIC_DRAIN") player:cast("MAGE_ELEMENTAL_CATALYST") end end
waitForLoadingScreen();
bossbuff()
player:cast("ROGUE_HIDE")
waitForLoadingScreen(); yrest(800) SlashCommand("ILG destroy"); yrest(200) SlashCommand("ILG inv"); yrest(200) SlashCommand("ILG inv");
